mapper.select:
SELECT
  (SELECT name_ FROM aos_sys_org WHERE id_ = aos_sys_user.org_id_) AS org_name_,
  (SELECT name_ FROM aos_sys_user WHERE id_ = aos_sys_user.creater_id_) AS creater_name_,
  
FROM aos_sys_user

	
	     AND (name_ LIKE '%${hotkey}%' OR account_ LIKE '%${hotkey}%')
	
	
		AND org_cascade_id_ like '${org_cascade_id_}%'
	
	
		AND org_id_ = #{org_id_}
	
	
		AND type_ != '2'  
	
	
		AND id_ != #{user_id_} 
	
	
		AND delete_flag_ = #{delete_flag_}

SELECT
  aos_sys_user_post.id_ AS user_post_id_,
  
FROM aos_sys_user, aos_sys_user_post WHERE aos_sys_user.id_ = aos_sys_user_post.user_id_ AND aos_sys_user_post.post_id_ = #{post_id_}

		AND (aos_sys_user.name_ LIKE '%${hotkey}%' OR aos_sys_user.account_ LIKE '%${hotkey}%')


		AND aos_sys_user.delete_flag_ = #{delete_flag_}

SELECT
    
FROM
	aos_sys_module , aos_sys_module_role
WHERE
	aos_sys_module.id_ = aos_sys_module_role.module_id_ AND aos_sys_module_role.grant_type_ = #{grant_type_} 
	AND aos_sys_module_role.role_id_ = #{role_id_} AND aos_sys_module.status_ = #{status_}

SELECT
  aos_sys_user_role.id_ AS user_role_id_,
  
FROM aos_sys_user, aos_sys_user_role WHERE aos_sys_user.id_ = aos_sys_user_role.user_id_ AND aos_sys_user_role.role_id_ = #{role_id_}

		AND (aos_sys_user.name_ LIKE '%${hotkey}%' OR aos_sys_user.account_ LIKE '%${hotkey}%')


       AND aos_sys_user.delete_flag_ = #{delete_flag_}

SELECT (SELECT name_ FROM aos_sys_org WHERE id_ = aos_sys_post.org_id_) AS org_name_, aos_sys_user_post.id_ AS user_post_id_,
FROM aos_sys_post, aos_sys_user_post WHERE aos_sys_post.id_ = aos_sys_user_post.post_id_ AND aos_sys_user_post.user_id_ = #{user_id_}
AND aos_sys_post.name_ LIKE '%${hotkey}%'

SELECT (SELECT name_ FROM aos_sys_user WHERE id_ = aos_sys_role.creater_id_) AS creater_name_, aos_sys_user_role.id_ AS user_role_id_,
FROM aos_sys_role, aos_sys_user_role WHERE aos_sys_role.id_ = aos_sys_user_role.role_id_ AND aos_sys_user_role.user_id_ = #{user_id_}
AND aos_sys_role.name_ LIKE '%${hotkey}%'

SELECT DISTINCT aos_sys_page_el_grant.id_, aos_sys_page_el_grant.el_id_, aos_sys_page_el_grant.grant_type_, aos_sys_page_el.dom_id_, aos_sys_page_el.type_, aos_sys_page_el.name_, aos_sys_page_el.module_id_, aos_sys_page_el.page_id_, aos_sys_module.name_ AS module_name_ FROM aos_sys_page_el_grant, aos_sys_page_el, aos_sys_module WHERE aos_sys_page_el_grant.el_id_ = aos_sys_page_el.id_ AND aos_sys_page_el.module_id_ = aos_sys_module.id_ AND aos_sys_page_el_grant.post_id_ IN ( SELECT post_id_ FROM aos_sys_user_post WHERE user_id_ = #{user_id_} )
AND aos_sys_page_el.module_id_ = #{module_id_} AND aos_sys_page_el.page_id_ = #{page_id_}
ORDER BY aos_sys_page_el.module_id_

SELECT DISTINCT aos_sys_page_el_grant.id_, aos_sys_page_el_grant.el_id_, aos_sys_page_el_grant.grant_type_, aos_sys_page_el.dom_id_, aos_sys_page_el.type_, aos_sys_page_el.name_, aos_sys_page_el.module_id_, aos_sys_page_el.page_id_, aos_sys_module.name_ AS module_name_ FROM aos_sys_page_el_grant, aos_sys_page_el, aos_sys_module WHERE aos_sys_page_el_grant.el_id_ = aos_sys_page_el.id_ AND aos_sys_page_el.module_id_ = aos_sys_module.id_ AND aos_sys_page_el_grant.role_id_ IN ( SELECT role_id_ FROM aos_sys_user_role WHERE user_id_ = #{user_id_} )
AND aos_sys_page_el.module_id_ = #{module_id_} AND aos_sys_page_el.page_id_ = #{page_id_}
ORDER BY aos_sys_page_el.module_id_

SELECT DISTINCT aos_sys_page_el_grant.id_, aos_sys_page_el_grant.el_id_, aos_sys_page_el_grant.grant_type_, aos_sys_page_el.dom_id_, aos_sys_page_el.type_, aos_sys_page_el.name_, aos_sys_page_el.module_id_, aos_sys_page_el.page_id_, aos_sys_module.name_ AS module_name_ FROM aos_sys_page_el_grant, aos_sys_page_el, aos_sys_module WHERE aos_sys_page_el_grant.el_id_ = aos_sys_page_el.id_ AND aos_sys_page_el.module_id_ = aos_sys_module.id_ AND aos_sys_page_el_grant.user_id_ = #{user_id_}
AND aos_sys_page_el.module_id_ = #{module_id_} AND aos_sys_page_el.page_id_ = #{page_id_}
ORDER BY aos_sys_page_el.module_id_

SELECT
  (SELECT name_ FROM aos_sys_module WHERE id_ = aos_sys_module_user_nav.module_id_) AS module_name_,
  
FROM aos_sys_module_user_nav

	
	    AND user_id_ = #{user_id_}
	
	
	    AND type_ = #{type_}
	

ORDER BY sort_no_

SELECT
FROM aos_sys_module , aos_sys_module_user_nav WHERE aos_sys_module.id_ = aos_sys_module_user_nav.module_id_ AND aos_sys_module_user_nav.user_id_ = #{user_id_} AND aos_sys_module.status_ = '1'
AND aos_sys_module_user_nav.type_ = #{type_}
AND aos_sys_module.is_leaf_ = #{is_leaf_}
ORDER BY aos_sys_module.sort_no_

SELECT
	COUNT(1)
FROM
	aos_sys_module_user_nav
WHERE user_id_ = #{user_id_}
AND type_ = '1'
AND module_id_ IN (
	SELECT id_ FROM aos_sys_module WHERE parent_id_ = #{module_id_}
 )
