mapper.select:
SELECT (SELECT name_ FROM aos_act_ru_execution WHERE id_ = aos_act_ru_task.proc_inst_id_ AND parent_id_ IS NULL) AS proc_inst_name_, (SELECT claim_time_ FROM aos_act_hi_taskinst WHERE id_ = aos_act_ru_task.id_) AS claim_time_, (SELECT name_ FROM aos_sys_user WHERE id_ = aos_act_ru_task.assignee_) AS assignee_user_ FROM aos_act_ru_task LEFT JOIN aos_act_ru_identitylink ON aos_act_ru_task.id_ = aos_act_ru_identitylink.task_id_ WHERE aos_act_ru_task.assignee_ = #{assignee_}
AND aos_act_ru_task.suspension_state_ = #{suspension_state_}
AND aos_act_ru_task.name_ LIKE '%${name_}%'
ORDER BY aos_act_ru_task.create_time_ DESC
